import React, { Fragment, useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import "./categoryItem.css";

const Category = () => {
    const [data, setData] = useState([]);
    const { id, name } = useParams(); // useParams hook'u ile id'yi al
    const [selectedProduct, setSelectedProduct] = useState(null);

    useEffect(() => {
        // Fetch data for a specific category using the provided id
        fetch(`/api/category_details/${id}`)
            .then((res) => res.json())
            .then((data) => setData(data))
            .catch((err) => console.log(err));
    }, [id]); // Fetch data whenever the id changes
    const handleCategoryClick = (id,name) => {
        setSelectedProduct(id,name);
    };


    return (
        <div className="content">
            {Array.isArray(data) && data.length > 0 ? (
                data.map((p) => (
                    <Fragment>
                    <Link style={{textDecoration:"none"}}  to={`/${name}/${p.id}`}>
                    {p.name}
                    </Link>
                    <div className='cart-item-container' key={p.id} onClick={() => handleCategoryClick(Category.id)}>
                    <img src={p.image_url} alt={p.name} />
                    <div className='item-details'>
                    <div className='name'>{p.name}</div>
                    <div className='price'>{p.price} tl</div>
                    </div>
                    <button className="button">
                    SEPETE EKLE
                    </button>
                    </div>
                    </Fragment>
                ))
            ) : (
                <div>No data available</div>
            )}
        </div>
    );
};

export default Category;
