import React, { useEffect, useState } from "react";
import "./Home.style.css";
import { Link } from "react-router-dom";
import Header from "../Header/Header";
import Category from "../categoryItem/Category";

const Home = () => {
    const categories = [
      {
        id: 1,
        title: "anakart",
        imageUrl: "https://img-itopya.mncdn.com/cdn/250/yeni-proje-2023-09-29t113240092-3df484.jpg",
      },
      {
        id: 2,
        title: "ekran-kartı",
        imageUrl: "https://img-itopya.mncdn.com/cdn/350/asus-tuf-gaming-geforce-rtx-3070-v2-oc-8gb-gddr6-265-bit-ekran-karti-6-a2fcef.jpg",
      },
      {
        id: 3,
        title: "işlemci",
        imageUrl: "https://img-itopya.mncdn.com/cdn/250/yeni-proje-2023-05-16t183232839-305b43-e69c4a-10b38c.jpg",
      },
      {
        id: 4,
        title: "monitor",
        imageUrl: "https://img-itopya.mncdn.com/cdn/250/yeni-proje-2023-08-28t151546164-36b5d8.jpg",
      },
      {
        id: 5,
        title: "ram",
        imageUrl: "https://img-itopya.mncdn.com/cdn/250/yeni-proje-2023-08-09t173226194-d61f2e.jpg",
      },
      {
          id: 6,
          title: "soğutucular",
          imageUrl: "https://img-itopya.mncdn.com/cdn/250/yeni-proje-2023-06-14t122829755-fc8257.jpg",
        },
        {
          id: 7,
          title: "termal-macun",
          imageUrl: "https://img-itopya.mncdn.com/cdn/250/yeni-proje-2023-01-25t175401199-968ac0.jpg",
        },
        {
          id: 8,
          title: "klavye",
          imageUrl: "https://img-itopya.mncdn.com/cdn/250/yeni-proje-2023-10-27t182922628-eddce9.jpg",
        },
        {
          id: 9,
          title: "mouse",
          imageUrl: "https://img-itopya.mncdn.com/cdn/250/yeni-proje-50-a7cd70.jpg",
        },
        {
          id: 10,
          title: "bilgisayar-kasaları",
          imageUrl: "https://img-itopya.mncdn.com/cdn/250/yeni-proje-2023-10-06t085733330-adc571.jpg",
        },
        {
          id: 11 ,
          title: "termal macun",
          imageUrl: "https://img-itopya.mncdn.com/cdn/350/yeni-proje-2023-01-25t174523207-3e0d45.jpg",
        },
    ];

    const [data, setData] = useState(null);

    useEffect(() => {
        fetch("/api/category")
            .then((res) => res.json())
            .then((data) => setData(data));
    }, []);

    return (
        <div>
            <Header />
            <div className="can">
                {categories &&
                    categories.map((p) => (
                        <div key={p.id} className="category-container">
                            <div className="background-image">
                                <img src={p.imageUrl} alt={p.title} />
                            </div>
                            <div className="category-body-container">
                                <Link to={`/${p.id}`}>
                                    {p.title}
                                </Link>
                                <p>İncele</p>
                            </div>
                        </div>
                    ))}
            </div>
            {data && <Category id={data.id} />}
        </div>
    );
};

export default Home;
