import React, { Fragment, useEffect, useState } from "react";
import { Link, Outlet } from "react-router-dom";
import "./Home.style.css";
import { useDispatch } from "react-redux";
import { setCategory } from "../../redux/ObserverComputer";

const Home = () => {

    const [ categories, setCategory] = useState(null)
    useEffect(() => {
      // Fetch data for a specific category using the provided id
      fetch("/api/category")
          .then((res) => res.json())
          .then((data) => setCategory(data))
          .catch((err) => console.log(err));
  }, []); 

  

 
    const dıspacth = useDispatch()

    return (
        <div>
            <div className="can">
            
                {categories.map((category) => (
                    <div key={category.id} className="category-container">
                        <img className="la" src={category.image_url} />
                        <div className="category-body-container">
                            <Fragment>
                                <Link style={{textDecoration:"none"}} onClick={() => dıspacth(setCategory(category.name))} to={`/category/${category.id}`}>
                                    {category.title}
                                </Link>
                                <p style={{textDecoration:"none"}} >
                                    İncele
                                </p>
                                <p>{category.description}</p>
                            </Fragment>
                        </div>
                    </div>
                ))}
            </div>
            <Outlet />
        </div>
    );
};

export default Home;
