import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import "./categoryItem.css";

const Category = () => {
    const [data, setData] = useState([]);
    const { id } = useParams(); // useParams hook'u ile id'yi al

    useEffect(() => {
        // Fetch data for a specific category using the provided id
        fetch(`/api/category_details/${id}`)
            .then((res) => res.json())
            .then((data) => setData(data))
            .catch((err) => console.log(err));
    }, [id]); // Fetch data whenever the id changes

    return (
        <div className="">
            {Array.isArray(data) && data.length > 0 ? (
                data.map((p) => (
                    <div className='cart-item-container' key={p.id}>
                        <img src={p.image_url} alt={p.name} />
                        <div className='item-details'>
                            <div className='name'>{p.name}</div>
                            <div className='price'>{p.price} tl</div>
                        </div>
                    </div>
                ))
            ) : (
                <div>No data available</div>
            )}
        </div>
    );
};

export default Category;
