import { useEffect, useState } from "react";
import { Button, Card, CardBody, CardSubtitle, CardText, CardTitle, ListGroup, ListGroupItem } from "reactstrap";

 function App (){
    const [data, setData] = useState(null);
    useEffect(() => {
        fetch("/api/category")
          .then((res) => res.json())
          .then((data) => setData(data));
      }, []);
    return(
        <ListGroup>
        {data &&
          data.map((p) => (
            <ListGroupItem>
              <Card
  style={{
    width: '18rem'
  }}
>
  <img
    alt="Sample"
    src={(p.image_url)}
  />
  <CardBody>
    <CardTitle tag="h5">
     {p.name}
    </CardTitle>
    <CardSubtitle
      className="mb-2 text-muted"
      tag="h6"
    >
     {p.price}
    </CardSubtitle>
    <CardText>
      Some quick example text to build on the card title and make up the bulk of the card‘s content.
    </CardText>
    <Button>
      Button
    </Button>
  </CardBody>
</Card>
                {(p.product)}
            </ListGroupItem>
          ))}

        </ListGroup>
    )
}
export default App;
