import { useState, useEffect } from "react";
import { Link } from "react-router-dom"; // Assuming you're using React Router for navigation
import "./sepet.style.css";
import { useSelector } from "react-redux";

const SepetSayfasi = () => {
  const cartSummary = useSelector((state) => state.cart.cartSummary);
  const [totalPrice, setTotalPrice] = useState(0);

  useEffect(() => {
    // Calculate total price when cartSummary changes
    if (cartSummary && cartSummary.length > 0) {
      const totalPrice = cartSummary.reduce(
        (accumulator, currentItem) => accumulator + currentItem.price,
        0
      );
      setTotalPrice(totalPrice);
    } else {
      setTotalPrice(0);
    }
  }, [cartSummary]);

  return (
    <div>
      {cartSummary && cartSummary.length > 0 ? (
        <div>
          <h2 className="baslık">Sepet İçeriği</h2>
          <div className="base">
            {cartSummary.map((item, index) => (
              <div key={index} className="urun">
                <img src={item.image_url} alt="" />
                <div className="item-name">{item.name}</div>
                <div>{item.price} tl</div>
              </div>
            ))}
          </div>
        </div>
      ) : (
        <h2 className="baslık">Sepetinizde ürün yok</h2>
      )}
      <div>Sepetinizin toplam tutarı {totalPrice} tl</div>
      <Link to="./">
        <button className="back-btn" on>Alışverişe devam et</button>
      </Link>
    </div>
  );
};

export default SepetSayfasi;
