import React, { Fragment, useEffect, useState } from "react";
import { Link, Outlet } from "react-router-dom";
import "./Home.style.css";
import { useDispatch } from "react-redux";
import { setCategory } from "../../redux/ObserverComputer";

const Home = () => {

    const [ categories, setCategories] = useState(null)
    useEffect(() => {
      // Fetch data for a specific category using the provided id
      fetch("/api/category")
          .then((res) => res.json())
          .then((data) => setCategories(data))
          .catch((err) => console.log(err));
  }, []); 

  useEffect(()=>{
    if (categories) {
        localStorage.setItem("category", JSON.stringify(categories));
      }
    }, [categories]);
  
    const storeCategories = JSON.parse(localStorage.getItem("category"));

 
    const dıspacth = useDispatch()

    return (
        <div>
            <div className="can">

                {categories && 
                  categories.map((category) => (
                    <div key={category.id} className="category-container">
                        <img className="la" src={category.image_url} style={{backgroundColor:"transparent"}}/>
                        <div className="category-body-container">
                            <Fragment>
                                <Link style={{textDecoration:"none",color:"black"}} onClick={() => dıspacth(setCategory(category.name))} to={`/category/${category.id}`}>
                                    {category.name}
                                </Link>
                                <p style={{textDecoration:"none"}} >
                                    İncele
                                </p>
                                <p>{}</p>
                            </Fragment>
                        </div>
                    </div>
                ))}
            </div>
            <Outlet />
        </div>
    );
};

export default Home;
