import { createSlice } from '@reduxjs/toolkit'



export const observeCart = createSlice({
  name: 'cart',

  initialState : {
   cartSummary : []
  },

  reducers: {
    addProduct : (state , action) =>{
      if(state.cartSummary.length == 0){
        state.cartSummary.push(action.payload)
      }else{
        if(state.cartSummary.filter((product) => product.category == action.payload.category).length == 0){
          state.cartSummary.push(action.payload)
        }
      }
      state.cartSummary.map((p) => console.log(p))
    },
    removeProduct: (state, action) => {
      const itemId = action.payload;
      state.cartSummary = state.cartSummary.filter((item) => item.id !== itemId);
    },
  },
})

// Action creators are generated for each case reducer function
export const { addProduct,removeProduct } = observeCart.actions

export default observeCart.reducer
