import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import "./Product.css";
import SepetSayfasi from "../sepet/SepetSayfasi";
import { addProduct } from "../../redux/observeCart";
import Alert from "./Alert";

import { AlertTitle } from "@mui/material";
import AlertComponent from "./Alert";


const ProductDetail = () => {
  const [product, setProduct] = useState(null);
  const currentCategory = useSelector((state) => state.action.currentCategory);
  const productId = useSelector((state) => state.action.productId);
  const [productDetails, setProductDetails] = useState(null);
  
  const dispacth = useDispatch();
  const [cartItems, setCartItems] = useState([]);
  

  useEffect(() => {
    // Fetch product details based on the productId and categoryName
    fetch(`/api/product_details/${productId}`) // Kategori adını da kullandık
      .then((res) => res.json())
      .then((data) => setProductDetails(data))
      .catch((err) => console.log(err));

    fetch(`/api/${currentCategory}/${productId}`) // Kategori adını da kullandık
      .then((res) => res.json())
      .then((data) => setProduct(data))
      .catch((err) => console.log(err));
  }, []);

  const filterKeys = (keys) => {
    // İlk ve son özellikleri filtrele
    const filteredKeys = keys.slice(1, -1);
    return filteredKeys;
  };
  useEffect(() => {
    if (product) {
      localStorage.setItem("product", JSON.stringify(product));
    }
  }, [product]);
  const storedProduct = JSON.parse(localStorage.getItem("product"));

  return (
    <div>
      {productDetails ? (
        productDetails.map((p) => (
          <div className="cart-item-content" key={p.id}>
            <img src={p.image_url} alt={p.name} />
            <div className="selam">
              <div className="item-detail">
                <stronge style={{fontSize:"20px"}} className="namee">{p.name}</stronge>
                
                <strong className="pricee">Fiyat {p.price} tl</strong>

                <button className="buttonn"  onClick={() => dispacth(addProduct(p))}>SEPETE EKLE</button>
              </div>
              <div className="etribute">
              {product &&
                product.map((item, index) => (
                  <div key={index} className="detail">
                  {filterKeys(Object.keys(item)).map((key) => (
                    <div key={key}>
                    <strong>{key} = </strong>
                    {item[key]}
                    </div>
                    ))}
                    <hr />
                    </div>
                    ))}
                    </div>
            </div>
          </div>
        ))): storedProduct ? (
          <div className="cart-item-content" key={storedProduct.id}>
            <img src={storedProduct.image_url} alt={storedProduct.name} />
            {/* Diğer ürün bilgileri */}
          </div>
        ) : (
          <p>Ürün bulunamadı.</p>
        )}
       <AlertComponent/>
    </div>
  );
};


export default ProductDetail;
