import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { useParams } from "react-router-dom";

const ProductDetail = () => {
  //const { productId, name } = useParams(); // Kategori adını aldık
  const [product, setProduct] = useState(null);

  useEffect(() => {
    // Fetch product details based on the productId and categoryName
    fetch(`/api/${currentCategory}/${productId}`) // Kategori adını da kullandık
      .then((res) => res.json())
      .then((data) => setProduct(data))
      .catch((err) => console.log(err));
  }, [productId,]);

  const currentCategory  = useSelector((state) => state.action.currentCategory)
  const productId  = useSelector((state) => state.action.productId)
  return (
    <div>
      {product ? (
        <div className='cart-item-container' style={{display:"flex",}}>
          <img src={product.image} alt={product.name} />
          <div className='item-details'>
            <h2 className='name'>{product.name}</h2>
            <p>{product.description}</p>
            <p className='price'>Price: ${product.price}</p>
            {/* Any other details you want to display */}
          </div>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default ProductDetail;
