import React, { Fragment, useState } from "react";
import { Link, Outlet } from "react-router-dom";
import "./Home.style.css";
import { useDispatch } from "react-redux";
import { setCategory } from "../../redux/ObserverComputer";

const Home = () => {
    const [categories] = useState([
    {
      id: 1,
      title: "işlemci",
      name: "processor",
      imageUrl: "https://img-itopya.mncdn.com/cdn/250/yeni-proje-2023-05-16t183232839-305b43-e69c4a-10b38c.jpg",
    },
      {
        id: 2,
        title: "anakart",
        name: "motherboard",
        imageUrl: "https://img-itopya.mncdn.com/cdn/250/yeni-proje-2023-09-29t113240092-3df484.jpg",
      },
      {
      id: 3,
      title: "ram",
      name: "ram",
      imageUrl: "https://img-itopya.mncdn.com/cdn/250/yeni-proje-2023-08-09t173226194-d61f2e.jpg",
    },
      {
        id: 4,
        title: "ekran-kartı",
        name: "graphics_card",
        imageUrl: "https://img-itopya.mncdn.com/cdn/350/asus-tuf-gaming-geforce-rtx-3070-v2-oc-8gb-gddr6-265-bit-ekran-karti-6-a2fcef.jpg",
      },
      {
        id: 5,
        title: "bilgisayar-kasaları",
        name: "computer_case",
        imageUrl: "https://img-itopya.mncdn.com/cdn/250/yeni-proje-2023-10-06t085733330-adc571.jpg",
      },
      {
        id: 6,
        title: "soğutucular",
        name: "case_fan",
        imageUrl: "https://img-itopya.mncdn.com/cdn/250/yeni-proje-2023-06-14t122829755-fc8257.jpg",
      },
      {
        id: 7,
        title: "kasa fanı",
        name:"case_fan",
        imageUrl: "https://img-itopya.mncdn.com/cdn/1000/yeni-proje-21-628569.jpg",
      },
      {
        id: 8 ,
        title: "termal macun",
        name: "cooler",
        imageUrl: "https://img-itopya.mncdn.com/cdn/350/yeni-proje-2023-01-25t174523207-3e0d45.jpg",
      },
      {
        id: 9,
        title: "klavye",
        name:"keyboard",
        imageUrl: "https://img-itopya.mncdn.com/cdn/250/yeni-proje-2023-10-27t182922628-eddce9.jpg",
      },
      {
      
        id: 10,
        title: "monitor",
        name: "monitor",
        imageUrl: "https://img-itopya.mncdn.com/cdn/250/yeni-proje-2023-08-28t151546164-36b5d8.jpg",
      },      
      {
          id: 11,
          title: "mouse",
          name:'mouse',
          imageUrl: "https://img-itopya.mncdn.com/cdn/250/yeni-proje-50-a7cd70.jpg",
        },
    ]);
    const [category , setCategory] = useState(null)
    useEffectt(() => {
      // Fetch data for a specific category using the provided id
      fetch("/api/category")
          .then((res) => res.json())
          .then((data) => setCategory(data))
          .catch((err) => console.log(err));
  }, [id]); 

    const [selectedCategory, setSelectedCategory] = useState(null);

 
    const dıspacth = useDispatch()

    return (
        <div>
            <div className="can">
                {categories.map((category) => (
                    <div key={category.id} className="category-container">
                        <img className="la" src={category.imageUrl} alt={category.title} />
                        <div className="category-body-container">
                            <Fragment>
                                <Link style={{textDecoration:"none"}} onClick={() => dıspacth(setCategory(category.name))} to={`/category/${category.id}`}>
                                    {category.title}
                                </Link>
                                <p style={{textDecoration:"none"}} >
                                    İncele
                                </p>
                                <p>{category.description}</p>
                            </Fragment>
                        </div>
                    </div>
                ))}
            </div>
            <Outlet />
        </div>
    );
};

export default Home;
