import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import "./Product.css";


const ProductDetail = () => {
  //const { productId, name } = useParams(); // Kategori adını aldık
  const [product, setProduct] = useState(null);
  const currentCategory  = useSelector((state) => state.action.currentCategory)
  const productId  = useSelector((state) => state.action.productId)
  const [productDetails, setproductDetails] = useState(null);
 

  useEffect(() => {
   // Fetch product details based on the productId and categoryName
    fetch(`/api/product_details/${productId}`) // Kategori adını da kullandık
      .then((res) => res.json())
      .then((data) => setproductDetails(data))
      .catch((err) => console.log(err));
  }, []);
  useEffect(()=>{
    fetch(`/api/${currentCategory}/${productId}`) // Kategori adını da kullandık
    .then((res) => res.json())
    .then((data) => setProduct(data))
    .catch((err) => console.log(err));
  },[])


  return (
    <div>
        {console.log(product)}
        {console.log(productDetails)}
      {productDetails && productDetails.map((p)=>
      <div className='cart-item-content'>
      <img src={p.image_url} alt={p.name} />
      <div> 
      <div className='item-detail'>
      <div className='namee'>{p.name}</div>
      <div className='pricee'>fiyatı {p.price} tl</div>
      
      <button className="buttonn">
      SEPETE EKLE
      </button>
      </div>
      </div>
      </div> )}
        
     </div>

  );
};

export default ProductDetail;
