import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import "./Product.css";
import SepetSayfasi from "../sepet/SepetSayfasi";

const ProductDetail = () => {
  const [product, setProduct] = useState(null);
  const currentCategory = useSelector((state) => state.action.currentCategory);
  const productId = useSelector((state) => state.action.productId);
  const [productDetails, setProductDetails] = useState(null);
  const [sayac,setSayac]=useState(0);
  const [cartItems, setCartItems] = useState([]);
  const history = useHistory();

  useEffect(() => {
    // Fetch product details based on the productId and categoryName
    fetch(`/api/product_details/${productId}`) // Kategori adını da kullandık
      .then((res) => res.json())
      .then((data) => setProductDetails(data))
      .catch((err) => console.log(err));

    fetch(`/api/${currentCategory}/${productId}`) // Kategori adını da kullandık
      .then((res) => res.json())
      .then((data) => setProduct(data))
      .catch((err) => console.log(err));
  }, []);

  const filterKeys = (keys) => {
    // İlk ve son özellikleri filtrele
    const filteredKeys = keys.slice(1, -1);
    return filteredKeys;
  };
   const incerement=()=>{
    if(sayac==0){
      alert("eksi olmaz")
    }else{
      setSayac(sayac-1);
    }
   }
   const decerement=()=>{
    setSayac(sayac+1);
   }

   const addToCart = (product) => {
    setCartItems([...cartItems, { ...product, quantity: sayac }]);
    history.push("/sepet");
  };
  return (
    <div>
      {console.log(product)}
      {console.log(productDetails)}
      {productDetails &&
        productDetails.map((p) => (
          <div className="cart-item-content" key={p.id}>
            <img src={p.image_url} alt={p.name} />
            <div className="selam">
              <div className="item-detail">
                <stronge style={{fontSize:"20px"}} className="namee">{p.name}</stronge>
                <div>
                <strong style={{fontSize:"20px",padding:"5px"}} onClick={incerement}>-</strong>
                <strong style={{fontSize:"20px",padding:"5px"}}>{sayac}</strong>
                <strong style={{fontSize:"20px",padding:"5px"}} onClick={decerement}>+</strong>
                </div>
                <strong className="pricee">fiyatı {p.price} tl</strong>

                <button className="buttonn" onClick={() => addToCart(p)} >SEPETE EKLE</button>
                <SepetSayfasi cartItems={cartItems} />
              </div>
              <div className="etribute">
              {product &&
                product.map((item, index) => (
                  <div key={index} className="detail">
                  {filterKeys(Object.keys(item)).map((key) => (
                    <div key={key}>
                    <strong>{key} = </strong>
                    {item[key]}
                    </div>
                    ))}
                    <hr />
                    </div>
                    ))}
                    </div>
            </div>
          </div>
        ))}
    </div>
  );
};

export default ProductDetail;
