import { createSlice } from '@reduxjs/toolkit'



export const observeCart = createSlice({
  name: 'cart',

  initialState : {
   cartSummary : []
  },

  reducers: {
    addProduct : (state , action) =>{
      state.cartSummary.forEach(element => {
        if(element.category.id != action.payload.product.category){
          state.cartSummary.push(action.payload)
        }
      });
      console.log(state.cartSummary)
    },

    
  },
})

// Action creators are generated for each case reducer function
export const { addProduct } = observeCart.actions

export default observeCart.reducer
