import { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom"; // Import useNavigate
import "./sepet.style.css";
import { useDispatch, useSelector } from "react-redux";
import { removeProduct } from "../../redux/observeCart";  
import AlertComponent from "../product/Alert";

const SepetSayfasi = () => {
  const cartSummary = useSelector((state) => state.cart.cartSummary);
  const [totalPrice, setTotalPrice] = useState(0);
  const navigate = useNavigate(); // Access the navigate function
  const dispacth = useDispatch();
  useEffect(() => {
    if (cartSummary && cartSummary.length > 0) {
      const totalPrice = cartSummary.reduce(
        (accumulator, currentItem) => accumulator + parseInt(currentItem.price),
        0
      );
      setTotalPrice(totalPrice);
    } else {
      setTotalPrice(0);
    }
  }, [cartSummary]);
 
  useEffect(() => {
    // Check if cartSummary and storedProduct have data
    if (cartSummary && cartSummary.length > 0 && storedProduct && storedProduct.length > 0) {
      const updatedCart = cartSummary.filter((cartItem) => {
        // Check if the item in cartSummary is not already in storedProduct
        return !storedProduct.some((storedItem) => storedItem.id === cartItem.id);
      });
  
      // Update localStorage with the updated cart
      localStorage.setItem("product", JSON.stringify([...storedProduct, ...updatedCart]));
    } else if (cartSummary && cartSummary.length > 0) {
      // If there is no storedProduct, just store the cartSummary in localStorage
      localStorage.setItem("product", JSON.stringify(cartSummary));
    }
  }, [cartSummary, storedProduct]);

  const handleContinueShopping = () => {
    navigate(-3); // Go back when the button is clicked
  };
  const handleRemoveItem = (itemId) => {
   dispacth(removeProduct(itemId));
  };

  return (
    <div>
    {
      cartSummary && cartSummary.length > 0 ? (
        <div>
          <h2 className="baslık">Sepet İçeriği</h2>
          <div className="base">
            {cartSummary.map((item, index) => (
              <div key={index} className="urun">
                <img src={item.image_url} alt="" />
                <div className="item-name">{item.name}</div>
                <div>{parseInt(item.price)} tl</div>
                <span className="material-symbols-outlined" onClick={() => handleRemoveItem(item.id)}>
                  delete
                </span>
              </div>
            ))}
          </div>
        </div>
      ) : (
        storedProduct && storedProduct.length > 0 ? (
          <div>
            <h2 className="baslık">Sepet İçeriği</h2>
            <div className="base">
              {storedProduct.map((item, index) => (
                <div key={index} className="urun">
                  <img src={item.image_url} alt="" />
                  <div className="item-name">{item.name}</div>
                  <div>{parseInt(item.price)} tl</div>
                  <span className="material-symbols-outlined" onClick={() => handleRemoveItem(item.id)}>
                    delete
                  </span>
                </div>
              ))}
            </div>
          </div>
        ) : (
          <h2 className="baslık">Sepetinizde ürün yok</h2>
        )
      )
    }
      <div className="tutar">
       <div className="price" > toplam tutar  =  {totalPrice} tl</div>
      <button className="back-btn" onClick={handleContinueShopping}>
      Alışverişe devam et
      </button>
      </div>
      <AlertComponent/>
    </div>
  );
};

export default SepetSayfasi;
